import { Fragment, useState, useMemo } from 'react';
import { JsonForms } from '@jsonforms/react';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import './App.css';
import {
  materialCells,
  materialRenderers,
} from '@jsonforms/material-renderers';
import RatingControl from './RatingControl';
import ratingControlTester from './ratingControlTester';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles({
  container: {
    padding: '1em',
    width: '100%',
  },
  title: {
    textAlign: 'center',
    padding: '0.25em',
  },
  dataContent: {
    display: 'flex',
    justifyContent: 'center',
    borderRadius: '0.25em',
    backgroundColor: '#cecece',
    marginBottom: '1rem',
  },
  resetButton: {
    margin: 'auto !important',
    display: 'block !important',
  },
  demoform: {
    margin: 'auto',
    padding: '1rem',
  },
});

const initialData = {
  name: 'Amanda',
  description: 'Long description',
};

const initialJsonSchema = {
  type: 'object',
  properties: {
    name: {
      type: 'string',
      minLength: 1,
    },
    description: {
      title: 'Long Description',
      type: 'string',
    },
  },
  required: ['name', 'due_date'],
};

const initialUiJsonSchema = {
  type: 'VerticalLayout',
  elements: [
    {
      type: 'Control',
      scope: '#/properties/name',
    },
    {
      type: 'Control',
      scope: '#/properties/description',
      options: {
        multi: true,
      },
    },
  ],
};

const renderers = [
  ...materialRenderers,
  { tester: ratingControlTester, renderer: RatingControl },
];

const App = () => {
  const classes = useStyles();
  const [data, setData] = useState<any>(initialData);
  const [jsonSchema, setJsonSchema] = useState<any>(initialJsonSchema);
  const [uiJsonSchema, setuiJsonSchema] = useState<any>(initialUiJsonSchema);

  const stringifiedData = useMemo(() => JSON.stringify(data, null, 2), [data]);
  const stringifiedJsonSchema = JSON.stringify(jsonSchema, null, 2);
  const stringifiedUiJsonSchema = JSON.stringify(uiJsonSchema, null, 2);

  const addNewField = () => {
    let newJsonSchema = jsonSchema;
    newJsonSchema.properties['newField'] = {
      type: 'string',
      minLength: 1,
    };

    // newJsonSchema = {
    //   type: 'object',
    //   properties: {
    //     name: {
    //       type: 'string',
    //       minLength: 1,
    //     },
    //     description: {
    //       title: 'Long Description',
    //       type: 'string',
    //     },
    //     newField: {
    //       title: 'New Field 1 (With Rule)',
    //       type: 'string',
    //     },
    //     newField2: {
    //       title: 'New Field 2',
    //       type: 'string',
    //     },
    //   },
    //   required: ['name', 'due_date'],
    // };

    setJsonSchema(newJsonSchema);

    const newUiSchema = uiJsonSchema;
    newUiSchema.elements.push(
      {
        type: 'Control',
        scope: '#/properties/newField',
        rule: {
          effect: 'HIDE',
          condition: {
            scope: '#/properties/name',
            schema: {
              const: 'John',
            },
          },
        },
      },
      {
        type: 'Control',
        scope: '#/properties/newField2',
      }
    );
    setuiJsonSchema(newUiSchema);
  };

  return (
    <Fragment>
      <Grid
        container
        justifyContent={'center'}
        spacing={1}
        className={classes.container}
      >
        <Grid style={{ backgroundColor: '#eeeeee' }} item sm={6}>
          <Typography variant={'h6'} className={classes.title}>
            Survey Data
          </Typography>
          <div style={{ fontSize: '12px' }} className={classes.dataContent}>
            <pre id='boundData'>{stringifiedData}</pre>
          </div>
          <Typography variant={'h6'} className={classes.title}>
            JSON Schema
          </Typography>
          <div style={{ fontSize: '12px' }} className={classes.dataContent}>
            <pre id='boundData'>{stringifiedJsonSchema}</pre>
          </div>
          <Typography variant={'h6'} className={classes.title}>
            UI Schema
          </Typography>
          <div style={{ fontSize: '12px' }} className={classes.dataContent}>
            <pre id='boundData'>{stringifiedUiJsonSchema}</pre>
          </div>
        </Grid>
        <Grid item sm={6}>
          <Typography variant={'h6'} className={classes.title}>
            Rendered Form
          </Typography>
          <div className={classes.demoform}>
            <JsonForms
              schema={jsonSchema}
              uischema={uiJsonSchema}
              data={data}
              renderers={renderers}
              cells={materialCells}
              onChange={({ errors, data }) => setData(data)}
            />
            <button className='blueButton' onClick={addNewField}>
              Add Additional Fields
            </button>
          </div>
        </Grid>
      </Grid>
    </Fragment>
  );
};

export default App;
