import React, { useState } from 'react';
import { Toggle as ToggleCl, Label, IconTooltip } from 'cl2-component-library';

interface RatingProps {
  id?: string;
  value: boolean;
  label: string;
  description?: string;
  updateValue: (newValue: boolean) => void;
}

export const Toggle: React.FC<RatingProps> = ({
  id,
  value,
  updateValue,
  label,
  description,
}) => {
  const [checked, setChecked] = useState<boolean>(value);

  return (
    <div id='#/properties/toggle' className='toggle'>
      <Label>Rating</Label>
      <div>
        <ToggleCl
          label={label}
          checked={checked}
          onChange={() => {
            updateValue(!value);
            setChecked(!value);
          }}
        />
      </div>
      <IconTooltip content={description} />
    </div>
  );
};
