import { Fragment, useState, useMemo } from 'react';
import { JsonForms } from '@jsonforms/react';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import './App.css';
import {
  materialCells,
  materialRenderers,
} from '@jsonforms/material-renderers';
import RatingControl from './RatingControl';
import ratingControlTester from './ratingControlTester';
import { makeStyles } from '@mui/styles';

let counter = 1;

const useStyles = makeStyles({
  container: {
    padding: '1em',
    width: '100%',
  },
  title: {
    textAlign: 'center',
    padding: '0.25em',
  },
  dataContent: {
    display: 'flex',
    justifyContent: 'center',
    borderRadius: '0.25em',
    backgroundColor: '#cecece',
    marginBottom: '1rem',
  },
  resetButton: {
    margin: 'auto !important',
    display: 'block !important',
  },
  demoform: {
    margin: 'auto',
    padding: '1rem',
  },
});

const initialData = {
  name: 'Amanda',
  description: 'Long description',
};

const initialJsonSchema = {
  type: 'object',
  properties: {
    name: {
      type: 'string',
      minLength: 1,
    },
    description: {
      title: 'Long Description',
      type: 'string',
    },
  },
  required: ['name', 'due_date'],
};

const initialUiJsonSchema = {
  type: 'VerticalLayout',
  elements: [
    {
      type: 'Control',
      scope: '#/properties/name',
    },
    {
      type: 'Control',
      scope: '#/properties/description',
      options: {
        multi: true,
      },
    },
  ],
};

const renderers = [
  ...materialRenderers,
  { tester: ratingControlTester, renderer: RatingControl },
];

const App = () => {
  const classes = useStyles();
  const [data, setData] = useState<any>(initialData);
  const [jsonSchema, setJsonSchema] = useState<any>(initialJsonSchema);
  const [uiJsonSchema, setuiJsonSchema] = useState<any>(initialUiJsonSchema);

  const stringifiedData = useMemo(() => JSON.stringify(data, null, 2), [data]);
  const stringifiedJsonSchema = JSON.stringify(jsonSchema, null, 2);
  const stringifiedUiJsonSchema = JSON.stringify(uiJsonSchema, null, 2);

  /**********************************************************************
   Load in the multi-step example schema
  **********************************************************************/
  const useMultistepExample = () => {
    setJsonSchema({
      type: 'object',
      properties: {
        person: {
          title: 'Person',
          type: 'object',
          properties: {
            firstName: {
              type: 'string',
            },
            lastName: {
              type: 'string',
            },
            age: {
              description: 'Age in years',
              type: 'integer',
              minimum: 0,
            },
            shippingAddress: {
              $ref: '#/properties/address/properties/addressId',
            },
          },
          required: ['firstName', 'lastName'],
        },
        address: {
          title: 'Order',
          type: 'object',
          properties: {
            addressId: {
              type: 'string',
              label: 'Address Type',
              enum: ['Home Address 1', 'Home Address 2', 'Workplace'],
            },
            street: {
              type: 'string',
            },
            city: {
              type: 'string',
            },
            zipCode: {
              type: 'string',
            },
          },
        },
      },
    });
  };

  /**********************************************************************
   Add field with a rule
  **********************************************************************/
  const addFieldWithRule = () => {
    let newJsonSchema = jsonSchema;
    const newField = 'newField' + counter.toString() + ' w Rule';
    newJsonSchema.properties[newField] = {
      type: 'string',
      minLength: 1,
    };
    const temp = {};
    Object.assign(temp, newJsonSchema);
    setJsonSchema(temp);
    // https://github.com/rjsf-team/react-jsonschema-form/issues/517

    const newUiSchema = uiJsonSchema;
    const newScope = '#/properties/' + newField;
    newUiSchema.elements.push({
      type: 'Control',
      scope: newScope,
      rule: {
        effect: 'HIDE',
        condition: {
          scope: '#/properties/name',
          schema: {
            const: 'John',
          },
        },
      },
    });
    setuiJsonSchema(newUiSchema);
    counter++;
  };

  /**********************************************************************
   Add new text or number field
  **********************************************************************/
  const addNewField = (type: string) => {
    if (type === 'text') {
      let newJsonSchema = jsonSchema;
      const newField = 'newField' + counter.toString() + ': Text';
      newJsonSchema.properties[newField] = {
        type: 'string',
        minLength: 1,
      };
      const temp = {};
      Object.assign(temp, newJsonSchema);
      setJsonSchema(temp);
      // https://github.com/rjsf-team/react-jsonschema-form/issues/517

      const newUiSchema = uiJsonSchema;
      const newScope = '#/properties/' + newField;
      newUiSchema.elements.push({
        type: 'Control',
        scope: newScope,
      });
      setuiJsonSchema(newUiSchema);
    }
    if (type === 'number') {
      let newJsonSchema = jsonSchema;
      const newField = 'newField' + counter.toString() + ': Number';
      newJsonSchema.properties[newField] = {
        type: 'number',
      };
      const temp = {};
      Object.assign(temp, newJsonSchema);
      setJsonSchema(temp);
      // https://github.com/rjsf-team/react-jsonschema-form/issues/517

      const newUiSchema = uiJsonSchema;
      const newScope = '#/properties/' + newField;
      newUiSchema.elements.push({
        type: 'Control',
        scope: newScope,
      });
      setuiJsonSchema(newUiSchema);
    }
    counter++;
  };

  return (
    <Fragment>
      <Grid
        container
        justifyContent={'center'}
        spacing={1}
        className={classes.container}
      >
        <Grid style={{ backgroundColor: '#eeeeee' }} item sm={6}>
          <Typography variant={'h6'} className={classes.title}>
            Survey Data
          </Typography>
          <div style={{ fontSize: '12px' }} className={classes.dataContent}>
            <pre id='boundData'>{stringifiedData}</pre>
          </div>
          <Typography variant={'h6'} className={classes.title}>
            JSON Schema
          </Typography>
          <div style={{ fontSize: '12px' }} className={classes.dataContent}>
            <pre id='boundData'>{stringifiedJsonSchema}</pre>
          </div>
          <Typography variant={'h6'} className={classes.title}>
            UI Schema
          </Typography>
          <div style={{ fontSize: '12px' }} className={classes.dataContent}>
            <pre id='boundData'>{stringifiedUiJsonSchema}</pre>
          </div>
        </Grid>
        <Grid item sm={6}>
          <button className='blueButton' onClick={() => useMultistepExample()}>
            Multi-step Example
          </button>
          <Typography variant={'h6'} className={classes.title}>
            Rendered Form
          </Typography>
          <div className={classes.demoform}>
            <JsonForms
              schema={jsonSchema}
              uischema={uiJsonSchema}
              data={data}
              renderers={renderers}
              cells={materialCells}
              onChange={({ errors, data }) => setData(data)}
            />
            <button className='blueButton' onClick={() => addNewField('text')}>
              Add Text Field
            </button>
            <button
              className='orangeButton'
              onClick={() => addNewField('number')}
            >
              Add Number Field
            </button>
            <button className='greenButton' onClick={addFieldWithRule}>
              Add Field With Rule
            </button>
          </div>
        </Grid>
      </Grid>
    </Fragment>
  );
};

export default App;
