import React from 'react';
import { render, screen } from '../../utils/testUtils/rtl';
import Tag from '.';

describe('<Tag />', () => {
  it('renders', () => {
    render(
      <Tag text="Test Tag label" isAutoTag isSelected>
        Test Tag
      </Tag>
    );
    expect(screen.getByText('Test Tag')).toBeInTheDocument();
    expect(screen.getByText('Test Tag label')).toBeInTheDocument();
  });
});
